mapper.select:
select 

from bookcase
where UID = #{uid,jdbcType=VARCHAR}
  and NID = #{nid,jdbcType=INTEGER}

mapper.delete:
delete from bookcase
where UID = #{uid,jdbcType=VARCHAR}
  and NID = #{nid,jdbcType=INTEGER}

mapper.update:
update bookcase

  
    Date = #{date,jdbcType=TIMESTAMP},
  
  
    Chapter = #{chapter,jdbcType=VARCHAR},
  

where UID = #{uid,jdbcType=VARCHAR}
  and NID = #{nid,jdbcType=INTEGER}

update bookcase
set Date = #{date,jdbcType=TIMESTAMP},
  Chapter = #{chapter,jdbcType=VARCHAR}
where UID = #{uid,jdbcType=VARCHAR}
  and NID = #{nid,jdbcType=INTEGER}
